						JSON.stringify(data);

						var obj = $.parseJSON(data);

						var numetu = "";
						var nom = "";
						var prenom = "";
						var email_1 = "";
						var email_2 = "";
						var voeu_effectue = '';
						var location_reservation = '';
						var disabled = '';
						var indication_voeu = '';

						$.each(obj, function (index, item) {

							$.each(item, function (key, value) {

								switch (key) {
								case 'numetu' :
									numetu = value;
									break;
								case 'nom' :
									nom = value;
									break;
								case 'prenom' :
									prenom = value;
									break;
								case 'email_1' :
									email_1 = value;
									break;
								case 'email_2' :
									email_2 = value;
									if (email_2.length>0) {
										email_2 = " , "+email_2;
									}
									break;
								case 'voeu_effectue' :
									voeu_effectue = value;
									break;
								case 'location_reservation' :
									location_reservation = value;
									switch (location_reservation) {
									case "0" :
										disabled = "";
										indication_voeu = "";
										break;
									case "1" :
										disabled = " disabled";
										indication_voeu = " : AVEC DÉJÀ UNE RÉSERVATION";
										break;
// 									case "2" :
// 										disabled = " disabled";
// 										indication_voeu = " : AVEC UNE LOCATION EN COURS";
// 										break;
// 									case "3" :
// 									case "4" :
// 										disabled = " disabled";
// 										indication_voeu = " : LOCATAIRE NON PRIORITAIRE";
// 										break;
									case "5" :
										disabled = " disabled";
										indication_voeu = "";
										break;
									default :
										if (voeu_effectue=="1") {
											disabled = " disabled";
											indication_voeu = " : DEMANDE DE RÉSERVATION DÉJÀ ÉFFECTUÉ";
										}
										else {
											disabled = "";
											indication_voeu = "";
										}
									}
									if (numetu==42218) {
										selected = ' selected';
									}
									else {
										selected = '';
									}
									options_1 += '<option value="'+numetu+'"'+disabled+selected+'>'+nom+' '+prenom+' - '+email_1+email_2+indication_voeu+'</option>';
									options += '<option value="'+numetu+'"'+disabled+'>'+nom+' '+prenom+' - '+email_1+email_2+indication_voeu+'</option>';
									break;
								}

							});

						});
					}

