	$("#check_logement_4CD3121").on("click", function(){
		if ($("#check_logement_4CD3121").is(':checked')) {

			$("#formulaire_voeu").css('display', 'block');
			$("#submit_reservation_error").css('display', 'none');
			
			$("#keyid_reservation").val('4CD3121');
			var message_affectation = $("#message_affectation_4CD3121").val();
			
			var keyid = $("#keyid_reservation").val();
			var residence = keyid.substr(0,1);

			var i = 0;
			var typelogem = "";
			var nb_occupants = 0;
// 			var nologem = "";
			var html_tds = [];
// 			var options_keyid = "";
			var pmr = 0;
			var boursier_prioritaire = 0;
			var est_caution_solidaire = 0;
			$("#tr_logement_4CD3121 td").each( function() {
				if (i>0) {
					html_tds[i] = $(this).html();
				}
				switch (i) {
// 				case 1 :
// 					nologem = html_tds[i];
// 					break;
// 				case 2 :
// 					
// 					var keyids = html_tds[i].split("<br>");
// 					$.each( keyids, function( index, keyid ) {
// 						options_keyid += '<option value="'+keyid+'">'+keyid+'</option>';
// 					});
// 					break;
				case 1 :
					typelogem = html_tds[i];
					break;
				case 3 :
					if (html_tds[i].trim()=='<span class="badge badge-success">Oui</span><br>Solidaire') {
						est_caution_solidaire = 1;
					}
					break;
				case 4 :
					nb_occupants = parseInt( html_tds[i] );
					break;
				case 5 :
					if (html_tds[i]=='<span class="badge badge-success">Oui</span>') {
						pmr = 1;
					}
					break;
				case 8 :
					if (html_tds[i]=='<span class="badge badge-success">Oui</span>') {
						boursier_prioritaire = 1;
					}
					break;
				}
				i ++;
			});

			var i = 1;
			$("#tr_formulaire_voeu td").each( function() {
				$(this).html( html_tds[i] );
				i ++;
			});

			if ((pmr==1)&&
				(0!=1)) {
				$("#alert_logement_pmr").css('display', 'block');
			}
			else {
				$("#alert_logement_pmr").css('display', 'none');
			}
			
			if ((boursier_prioritaire==1)&&
				(0!=1)) {
				$("#alert_logement_boursier").css('display', 'block');
			}
			else {
				$("#alert_logement_boursier").css('display', 'none');
			}

			if ((typelogem=="T2")&&
				(residence=="1")) {
				$("#alert_logement_res_1_t2").css('display', 'block');
			}
			else {
				$("#alert_logement_res_1_t2").css('display', 'none');
			}
			
			if (message_affectation!="") {
				$("#alert_message_affectation").css('display', 'block');
				$("#message_affectation").html(message_affectation);
			}
			else {
				$("#message_affectation").html('');
				$("#alert_message_affectation").css('display', 'none');
			}

			$("#nb_occupants").val( nb_occupants );
			$("#est_caution_solidaire").val( est_caution_solidaire );
// 			$("#nologem").val( nologem );

			if (est_caution_solidaire==0) {

				$("#formulaire_voeu_occupants_content").css('display', 'none');
				
			}
			else {

				$("#formulaire_voeu_occupants_content").css('display', 'block');
				
				var label_occupant = "Colocataires";
				$("#formulaire_voeu_label_occupants").html( label_occupant );
				
				$("#chargement_colocataires").css('display', 'block');
				$("#formulaire_voeu_occupants").html( '' );
			
				var selected = '';
				var options_1 = '';
				var options = '';
				var html_occupants = '';
			
			 	$.post("https://logement.cesal-residentiel.fr/espace-resident/cesal_ajax_etudiants_pour_voeux.php", { ngtl: '1', arrivee: '2023-03-24' }).done(function (data) {

					if (data.length > 0) {

						JSON.stringify(data);

						var obj = $.parseJSON(data);

						var numetu = "";
						var nom = "";
						var prenom = "";
						var email_1 = "";
						var email_2 = "";
						var voeu_effectue = '';
						var location_reservation = '';
						var disabled = '';
						var indication_voeu = '';

						$.each(obj, function (index, item) {

							$.each(item, function (key, value) {

								switch (key) {
								case 'numetu' :
									numetu = value;
									break;
								case 'nom' :
									nom = value;
									break;
								case 'prenom' :
									prenom = value;
									break;
								case 'email_1' :
									email_1 = value;
									break;
								case 'email_2' :
									email_2 = value;
									if (email_2.length>0) {
										email_2 = " , "+email_2;
									}
									break;
								case 'voeu_effectue' :
									voeu_effectue = value;
									break;
								case 'location_reservation' :
									location_reservation = value;
									switch (location_reservation) {
									case "0" :
										disabled = "";
										indication_voeu = "";
										break;
									case "1" :
										disabled = " disabled";
										indication_voeu = " : AVEC DÉJÀ UNE RÉSERVATION";
										break;
// 									case "2" :
// 										disabled = " disabled";
// 										indication_voeu = " : AVEC UNE LOCATION EN COURS";
// 										break;
// 									case "3" :
// 									case "4" :
// 										disabled = " disabled";
// 										indication_voeu = " : LOCATAIRE NON PRIORITAIRE";
// 										break;
									case "5" :
										disabled = " disabled";
										indication_voeu = "";
										break;
									default :
