				$("#formulaire_voeu_occupants_content").css('display', 'none');
				
			}
			else {

				$("#formulaire_voeu_occupants_content").css('display', 'block');
				
				var label_occupant = "Colocataires";
				$("#formulaire_voeu_label_occupants").html( label_occupant );
				
				$("#chargement_colocataires").css('display', 'block');
				$("#formulaire_voeu_occupants").html( '' );
			
				var selected = '';
				var options_1 = '';
				var options = '';
				var html_occupants = '';
			
			 	$.post("https://logement.cesal-residentiel.fr/espace-resident/cesal_ajax_etudiants_pour_voeux.php", { ngtl: '1', arrivee: '2023-03-24' }).done(function (data) {

					if (data.length > 0) {

						JSON.stringify(data);

						var obj = $.parseJSON(data);

						var numetu = "";
						var nom = "";
						var prenom = "";
						var email_1 = "";
						var email_2 = "";
						var voeu_effectue = '';
						var location_reservation = '';
						var disabled = '';
						var indication_voeu = '';

						$.each(obj, function (index, item) {

							$.each(item, function (key, value) {

								switch (key) {
								case 'numetu' :
									numetu = value;
									break;
								case 'nom' :
									nom = value;
									break;
								case 'prenom' :
									prenom = value;
									break;
								case 'email_1' :
									email_1 = value;
									break;
								case 'email_2' :
									email_2 = value;
									if (email_2.length>0) {
										email_2 = " , "+email_2;
									}
									break;
								case 'voeu_effectue' :
									voeu_effectue = value;
									break;
								case 'location_reservation' :
									location_reservation = value;
									switch (location_reservation) {
									case "0" :
										disabled = "";
										indication_voeu = "";
										break;
									case "1" :
										disabled = " disabled";
										indication_voeu = " : AVEC DÉJÀ UNE RÉSERVATION";
										break;
// 									case "2" :
// 										disabled = " disabled";
// 										indication_voeu = " : AVEC UNE LOCATION EN COURS";
// 										break;
// 									case "3" :
// 									case "4" :
// 										disabled = " disabled";
// 										indication_voeu = " : LOCATAIRE NON PRIORITAIRE";
// 										break;
									case "5" :
										disabled = " disabled";
										indication_voeu = "";
										break;
									default :
										if (voeu_effectue=="1") {
											disabled = " disabled";
											indication_voeu = " : DEMANDE DE RÉSERVATION DÉJÀ ÉFFECTUÉ";
										}
										else {
											disabled = "";
											indication_voeu = "";
										}
									}
									if (numetu==42218) {
										selected = ' selected';
									}
									else {
										selected = '';
									}
									options_1 += '<option value="'+numetu+'"'+disabled+selected+'>'+nom+' '+prenom+' - '+email_1+email_2+indication_voeu+'</option>';
									options += '<option value="'+numetu+'"'+disabled+'>'+nom+' '+prenom+' - '+email_1+email_2+indication_voeu+'</option>';
									break;
								}

							});

						});
					}

					for(i=0;i<nb_occupants;i++) {

						var indicate_occupant = "Indiquez le locataire n°[NUMBER]";
						indicate_occupant = indicate_occupant.replace("[NUMBER]",(i+1));

						html_occupants += '<div class="row" style="width:100%;">';
						html_occupants += '<select form="action-validation_reservation" class="js-select2 form-control" id="numetu_'+(i+1)+'" name="numetu_'+(i+1)+'" style="width:66%;float:left" onchange="change_occupant(this.id);">';
						if (i==0) {
							html_occupants += '<option value="-1">--- '+indicate_occupant+' ---</option>';
							html_occupants += options_1;
						}
						else {
							html_occupants += '<option value="-1" selected>--- '+indicate_occupant+' ---</option>';
							html_occupants += options;
						}
						html_occupants += '</select>';
						html_occupants += '</div>';

					}
					
					$("#chargement_colocataires").css('display', 'none');
					$("#formulaire_voeu_occupants").html( html_occupants );

					jQuery(function(){
						App.initHelpers(['select2']);
					});
			
				});
				
			}
				
			$("html, body").stop().animate( { scrollTop: $("#formulaire_voeu").offset().top }, 1000);

		}
		else {
			$("#formulaire_voeu").css('display', 'none');
			$("html, body").stop().animate( { scrollTop: $("#niveau_4_C_E_5_select").offset().top }, 1000);
		}
	});

