								case 'nom' :
									nom = value;
									break;
								case 'prenom' :
									prenom = value;
									break;
								case 'email_1' :
									email_1 = value;
									break;
								case 'email_2' :
									email_2 = value;
									if (email_2.length>0) {
										email_2 = " , "+email_2;
									}
									break;
								case 'voeu_effectue' :
									voeu_effectue = value;
									break;
								case 'location_reservation' :
									location_reservation = value;
									switch (location_reservation) {
									case "0" :
										disabled = "";
										indication_voeu = "";
										break;
									case "1" :
										disabled = " disabled";
										indication_voeu = " : AVEC DÉJÀ UNE RÉSERVATION";
										break;
