										indication_voeu = "";
										break;
									default :
										if (voeu_effectue=="1") {
											disabled = " disabled";
											indication_voeu = " : DEMANDE DE RÉSERVATION DÉJÀ ÉFFECTUÉ";
										}
										else {
											disabled = "";
											indication_voeu = "";
										}
									}
									if (numetu==42218) {
										selected = ' selected';
									}
									else {
										selected = '';
									}
									options_1 += '<option value="'+numetu+'"'+disabled+selected+'>'+nom+' '+prenom+' - '+email_1+email_2+indication_voeu+'</option>';
									options += '<option value="'+numetu+'"'+disabled+'>'+nom+' '+prenom+' - '+email_1+email_2+indication_voeu+'</option>';
									break;
								}

							});

						});
					}

					for(i=0;i<nb_occupants;i++) {

						var indicate_occupant = "Indiquez le locataire n°[NUMBER]";
						indicate_occupant = indicate_occupant.replace("[NUMBER]",(i+1));

						html_occupants += '<div class="row" style="width:100%;">';
						html_occupants += '<select form="action-validation_reservation" class="js-select2 form-control" id="numetu_'+(i+1)+'" name="numetu_'+(i+1)+'" style="width:66%;float:left" onchange="change_occupant(this.id);">';
						if (i==0) {
							html_occupants += '<option value="-1">--- '+indicate_occupant+' ---</option>';
							html_occupants += options_1;
						}
						else {
							html_occupants += '<option value="-1" selected>--- '+indicate_occupant+' ---</option>';
							html_occupants += options;
						}
						html_occupants += '</select>';
						html_occupants += '</div>';

					}
					
					$("#chargement_colocataires").css('display', 'none');
					$("#formulaire_voeu_occupants").html( html_occupants );

					jQuery(function(){
						App.initHelpers(['select2']);
					});
			
				});
				
			}
				
			$("html, body").stop().animate( { scrollTop: $("#formulaire_voeu").offset().top }, 1000);

		}
		else {
			$("#formulaire_voeu").css('display', 'none');
			$("html, body").stop().animate( { scrollTop: $("#niveau_6_D_A_4_select").offset().top }, 1000);
		}
	});

