	function change_occupant( id ) {
		var libelle_etu = $('#select2-'+id+'-container').text();
		var tab_libelle_etu = libelle_etu.split(' - ');
		$('#parking_occupant_'+id).val(tab_libelle_etu[0]);
	}
	
	function click_parking( id ) {
		var tab_id = id.split("_");
		var residence = tab_id[1];
		var i_occupant = tab_id[2];

		if (residence!='4') {
			$("#parking_4_"+i_occupant).prop('checked', false);
		}
		if (residence!='5') {
			$("#parking_5_"+i_occupant).prop('checked', false);
		}
		if (residence!='6') {
			$("#parking_6_"+i_occupant).prop('checked', false);
		}

	}

	function submit_reservation() {
	
		$("#submit_reservation_error").css('display', 'none');

		var est_caution_solidaire = $("#est_caution_solidaire").val();
		var nb_occupants = $("#nb_occupants").val();
		var keyid_reservation = $("#keyid_reservation").val();
		var date_entree = $("#date_entree").val();
		var date_sortie = $("#date_sortie").val();
		var avec_preavis_a_generer = $("#avec_preavis_a_generer").val();

		var tab_msg_error = [];
		var nbr_erreurs=0;
		var email_signature_ok = 1;
		
		$("#keyid").val( keyid_reservation );
		
		if (avec_preavis_a_generer=="1") {
			var email_depot_garantie = $("#email_depot_garantie").val();
			if (email_depot_garantie=="") {
				email_signature_ok = 0;
			}
		}

		if (date_sortie=="") {

			nbr_erreurs++;
			tab_msg_error[nbr_erreurs] = "Vous devez indiquer une <STRONG>date de fin de bail souhaitée</STRONG>&nbsp;!";

		}
		else if (date_entree!="") {

			var tab_date_entree = date_entree.split("/");
			var date_entree_test = new Date(tab_date_entree[2], tab_date_entree[1], tab_date_entree[0]);

			var tab_date_sortie = date_sortie.split("/");
			var date_sortie_test = new Date(tab_date_sortie[2], tab_date_sortie[1], tab_date_sortie[0]);

			if (date_entree_test>=date_sortie_test) {

				var date_sortie_test_day = date_sortie_test.getDate().toString();
				if (date_sortie_test_day.length==1) date_sortie_test_day = "0" + date_sortie_test_day;

				var month = date_sortie_test.getMonth();
				var date_sortie_test_month = month.toString();
				if (date_sortie_test_month.length==1) date_sortie_test_month = "0" + date_sortie_test_month;


				var date_sortie_test_display = date_sortie_test_day + "/" + date_sortie_test_month + "/" + date_sortie_test.getFullYear();

				nbr_erreurs++;
				tab_msg_error[nbr_erreurs] = "La <STRONG>date de fin de bail souhaitée</STRONG> ne peut pas être antérieure ou égale à la <STRONG>d’arrivée</STRONG>&nbsp;!";

			}

		}
		else if (email_signature_ok==0) {

			nbr_erreurs++;
			tab_msg_error[nbr_erreurs] = "Vous devez renseigner un <STRONG>email pour la signature</STRONG>&nbsp;!";

		}
		
		if (est_caution_solidaire==1) {

			var est_dans_locataires=0;
			var locataires_renseignes=1;
			var locataires = [];

			for(i=0;i<nb_occupants;i++) {

				var numetu = parseInt( $("#numetu_"+(i+1)+" option:selected").val() );

				if (numetu==-1) {
					locataires_renseignes = 0;
					nbr_erreurs++;
					var roommates_missing = "Vous devez indiquer le <STRONG>colocataire n°[NUMBER]</STRONG>&nbsp;!";
					roommates_missing = roommates_missing.replace("[NUMBER]",(i+1));
					tab_msg_error[nbr_erreurs] = roommates_missing;
				}
				else if (locataires.indexOf(numetu)!=-1) {
					var nomlocataire = $("#numetu_"+(i+1)+" option:selected").text();
					nbr_erreurs++;
					var roommates_several_times= "Vous avez indiqué plusieurs fois le colocataire <STRONG>[ROOMMATE_NAME]</STRONG>&nbsp;!";
					roommates_several_times = roommates_several_times.replace("[ROOMMATE_NAME]",nomlocataire);
					tab_msg_error[nbr_erreurs] = roommates_several_times;
				}

				if (numetu==42218) {
					est_dans_locataires = 1;
				}

				locataires[i] = numetu;
			}

			if ((est_dans_locataires==0)&&
				(locataires_renseignes==1)) {
				nbr_erreurs++;
				tab_msg_error[nbr_erreurs] = "Vous devez être <STRONG>l’un des colocataires</STRONG>&nbsp;!";
			}

		}

		if (nbr_erreurs>0) {
			$("#submit_reservation_error").css('display', 'block');
			$("#submit_reservation_error_message").html( tab_msg_error.join("<br/>") );
		}
		else {
			$.confirm(	{	title: 'Validation de votre demande de réservation',
							content: 'Êtes-vous sûr de vouloir valider votre demande de réservation de logement avec les informations saisies ?<br/><br/>Une fois votre demande de réservation validée, vous ne pourrez plus la modifier.',
							icon: 'fa fa-check-square',
							type: 'blue',
							animation: 'scale',
							closeAnimation: 'scale',
							opacity: 0.5,
							buttons: {	confirm: {	text: 'Valider',
													btnClass: 'btn-blue',
													action: function() {
														$("#submit_reservation_error").css('display', 'none');
														$("#action-validation_reservation").submit();
													}
										},
										cancel: {	text: 'Annuler'
										}
									}
							});
		}
		
	}

</script>
